    private void jbCrearPDFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCrearPDFActionPerformed
        // TODO add your handling code here:
        try{
            miPDF.crearPDF(jtfTitulo.getText(), jtfAutor.getText(), jtfAsunto.getText(), jtfPalabrasClaves.getText(), jtaTexto.getText());
        }
        catch(Exception ex){
            JOptionPane.showMessageDialog(null, "No se puede crear PDF");
        }
    }//GEN-LAST:event_jbCrearPDFActionPerformed

