public String direccionar()
    {
        String so=System.getProperty("os.name");
        URL dir=llamar_parametros.class.getProtectionDomain().getCodeSource().getLocation();//esta linea hace referencia a la clase misma
        String[] direc=dir.getPath().split("/");
        int h=0;
        String res="";
        if (so.equalsIgnoreCase("linux"))
        {
            res="/"+direc[0];
        }
        else
        {
            res=direc[0];
        }

        for(h=1;h<direc.length-1;h++)
        {
            res+=direc[h]+"/";
        }

        return res;
    }
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       Map parametros = new HashMap();
        parametros.put("car", this.jTextField1.getText());//para poner los parametros que requiere el reporte ;)

            JasperReport report=null;
            try
            {

                report = JasperCompileManager.compileReport(this.direccionar().replaceAll("%20", " ") +"/src/alumnos_carnet.jrxml");
            }
            catch (Exception ex)
            {
            JOptionPane.showMessageDialog(this, "error al buscar "+ ex.getMessage());
            }
            Connection con;
            conexion c = new conexion();
            con = c.conectar();
            JasperPrint print=null;
        try
        {
            print = JasperFillManager.fillReport(report, parametros, con);
        }
        catch (JRException ex)
        {
           JOptionPane.showMessageDialog(this,"error al llenar "+ex.getMessage());
        }
        JasperViewer.viewReport(print);
        //this.dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

